mapper.select:
SELECT ${params.xColumn}, ROUND(sum(${params.yColumn}),1) total FROM jiankangshuju
 1=1 ${ew.sqlSegment}
group by ${params.xColumn}

select.if:
SELECT DATE_FORMAT(${params.xColumn},'%Y-%m-%d') ${params.xColumn}, sum(${params.yColumn}) total FROM jiankangshuju
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y-%m-%d')

SELECT DATE_FORMAT(${params.xColumn},'%Y-%m') ${params.xColumn}, sum(${params.yColumn}) total FROM jiankangshuju
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y-%m')

SELECT CONCAT(YEAR(${params.xColumn}), ' - ', ELT(QUARTER(${params.xColumn}), '第一季度', '第二季度', '第三季度', '第四季度')) as ${params.xColumn}, sum(${params.yColumn}) total FROM orders
 1=1 ${ew.sqlSegment}
group by CONCAT(YEAR(${params.xColumn}), ' - ', ELT(QUARTER(${params.xColumn}), '第一季度', '第二季度', '第三季度', '第四季度'))

SELECT DATE_FORMAT(${params.xColumn},'%Y') ${params.xColumn}, sum(${params.yColumn}) total FROM jiankangshuju
 1=1 ${ew.sqlSegment}
group by DATE_FORMAT(${params.xColumn},'%Y')

mapper.select:
SELECT ${params.column} , count(1) total FROM jiankangshuju
 1=1 ${ew.sqlSegment}
group by ${params.column}

SELECT '低血糖' as xuetang,case when t.低血糖 is null then 0 else t.低血糖 end total
from
(select
sum(case when xuetang <= 3.9 then 1 else 0 end) as 低血糖,        sum(case when xuetang >= 3.9 and xuetang <= 6.1 then 1 else 0 end) as 正常,        sum(case when xuetang >= 6.1 then 1 else 0 end) as 高血糖        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t
union all
SELECT '正常' as xuetang,case when t.正常 is null then 0 else t.正常 end total
from
(select
sum(case when xuetang <= 3.9 then 1 else 0 end) as 低血糖,        sum(case when xuetang >= 3.9 and xuetang <= 6.1 then 1 else 0 end) as 正常,        sum(case when xuetang >= 6.1 then 1 else 0 end) as 高血糖        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t
union all
SELECT '高血糖' as xuetang,case when t.高血糖 is null then 0 else t.高血糖 end total
from
(select
sum(case when xuetang <= 3.9 then 1 else 0 end) as 低血糖,        sum(case when xuetang >= 3.9 and xuetang <= 6.1 then 1 else 0 end) as 正常,        sum(case when xuetang >= 6.1 then 1 else 0 end) as 高血糖        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t

SELECT '低血压' as shousuoya,case when t.低血压 is null then 0 else t.低血压 end total
from
(select
sum(case when shousuoya <= 90 then 1 else 0 end) as 低血压,        sum(case when shousuoya >= 90 and shousuoya <= 140 then 1 else 0 end) as 正常,        sum(case when shousuoya >= 140 then 1 else 0 end) as 高血压        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t
union all
SELECT '正常' as shousuoya,case when t.正常 is null then 0 else t.正常 end total
from
(select
sum(case when shousuoya <= 90 then 1 else 0 end) as 低血压,        sum(case when shousuoya >= 90 and shousuoya <= 140 then 1 else 0 end) as 正常,        sum(case when shousuoya >= 140 then 1 else 0 end) as 高血压        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t
union all
SELECT '高血压' as shousuoya,case when t.高血压 is null then 0 else t.高血压 end total
from
(select
sum(case when shousuoya <= 90 then 1 else 0 end) as 低血压,        sum(case when shousuoya >= 90 and shousuoya <= 140 then 1 else 0 end) as 正常,        sum(case when shousuoya >= 140 then 1 else 0 end) as 高血压        from jiankangshuju
 1=1 ${ew.sqlSegment}
) t
